sun8i-h3-nanopi-neo.dts<pre><code>i2c@1c2ac00 {
    compatible = "allwinner,sun6i-a31-i2c";
    reg = &lt;0x1c2ac00 0x400&gt;;
    interrupts = &lt;0x00 0x06 0x04&gt;;
    clocks = &lt;0x03 0x3b&gt;;
    resets = &lt;0x03 0x2e&gt;;
    pinctrl-names = "default";
    pinctrl-0 = &lt;0x1a&gt;;
    status = "disabled";
    #address-cells = &lt;0x01&gt;;
    #size-cells = &lt;0x00&gt;;
    phandle = &lt;0x67&gt;;
    clock-frequency = &lt;0xF4240&gt;;
};
</code></pre>
